<?php/** * @version        2.0.0 * @package        Joomla * @subpackage     Event Booking * @author         Tuan Pham Ngoc * @copyright      Copyright (C) 2010 - 2015 Ossolution Team * @license        GNU/GPL, see LICENSE.php */// no direct accessdefined('_JEXEC') or die;JHtml::_('script', JUri::root().'media/com_eventbooking/assets/js/noconflict.js', false, false);	if ($showLocation) {	$width = (int) $config->map_width ;	if (!$width)	{		$width = 800 ;	}	$height = (int) $config->map_height ;	if (!$height)	{		$height = 600 ;	}	EventbookingHelperJquery::colorbox('eb-colorbox-map', $width.'px', $height.'px', 'true', 'false');}if (count($rows)) {?>	<div class="eb_event_list row" width="100%">		<?php			foreach ($rows as  $row) 			{			?>					<div class="col-sm-6 col-md-3 eb_event">					<div class="eb_event_inner">						<div class="eb_event_img"><img src="media/com_eventbooking/images/<?php echo $row->thumb; ?>" alt="<?php echo $row->title ; ?>" /></div>						<h4><a href="<?php echo JRoute::_(EventbookingHelperRoute::getEventRoute($row->id, 0, $itemId)); ?>" class="eb_event_link"><?php echo $row->title ; ?></a></h4>						<p><i class="icon-time"> </i><span class="event_date"><?php echo JHTML::_('date', $row->event_date, $config->event_date_format, null); ?></span></p>						<?php							if ($showCategory) 							{							?>								<p><i class="icon-folder"></i> <span><?php echo $row->categories ; ?></span></p>							<?php								}							if ($showLocation && strlen($row->location_name)) 							{							?>								<p><i class="icon-map-marker"></i> <a href="<?php echo JRoute::_('index.php?option=com_eventbooking&view=map&location_id='.$row->location_id.'&tmpl=component&format=html&Itemid='.$itemId); ?>" class="eb-colorbox-map"><?php echo $row->location_name ; ?></a></p>							<?php	 							}						?>																</div>				</div>			<?php			}		?>	</div><?php	} else {?>	<div class="eb_empty"><?php echo JText::_('EB_NO_UPCOMING_EVENTS') ?></div><?php	}?>